import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import { supabase } from '../supabase';
import dayjs from 'dayjs';

type Viagem = {
  data: string;
  sentido: 'ida' | 'volta';
  origem: string;
  km_inicial: number;
  hora_inicial: string;
  destino: string;
  hora_final: string;
};

type ViagemAgrupada = {
  id: string;
  data: string;
  ida?: Viagem;
  volta?: Viagem;
};

type GerarPdfParams = {
  motoristaNome: string;
  cavalo: string;
  carreta: string;
  horario: string;
  viagens: ViagemAgrupada[];
  semana: string;
};

export default async function gerarPdfDiarioDeBordo({
  motoristaNome,
  cavalo,
  carreta,
  horario,
  viagens,
  semana,
}: GerarPdfParams): Promise<string | null> {
  const doc = new jsPDF();

  // üñºÔ∏è Insira o base64 dos dois logos abaixo
  const logoEsquerdoBase64 = 'data:image/png;base64,'; // substitua por seu base64
  const logoDireitoBase64 = 'data:image/png;base64,';  // substitua por seu base64

  try {
    // Logo esquerdo (topo esquerdo)
    doc.addImage(logoEsquerdoBase64, 'PNG', 14, 10, 30, 15); // x, y, largura, altura
    // Logo direito (topo direito)
    doc.addImage(logoDireitoBase64, 'PNG', 165, 10, 30, 15);
  } catch (err) {
    console.warn('Erro ao adicionar as imagens. Verifique se os base64 est√£o corretos.');
  }

  // T√≠tulo centralizado
  doc.setFontSize(16);
  doc.text('DI√ÅRIO DE BORDO', 105, 20, { align: 'center' });

  // Informa√ß√µes do cabe√ßalho
  doc.setFontSize(10);
  doc.text(`Motorista: ${motoristaNome}`, 14, 30);
  doc.text(`Cavalo: ${cavalo}`, 14, 36);
  doc.text(`Carreta: ${carreta}`, 14, 42);
  doc.text(`Hor√°rio: ${horario}`, 14, 48);
  doc.text(`Semana: ${semana}`, 14, 54);

  const head = [
    [
      'Data',
      'Origem IDA',
      'Destino IDA',
      'Hora IDA',
      'KM IDA',
      'Origem VOLTA',
      'Destino VOLTA',
      'Hora VOLTA',
      'KM VOLTA',
    ],
  ];

  const formatHora = (hora: string) => hora?.slice(0, 5) || '';

  const body = viagens.map((v) => {
    const data = dayjs(v.data).format('DD/MM/YYYY');
    return [
      data,
      v.ida?.origem || '',
      v.ida?.destino || '',
      v.ida ? `${formatHora(v.ida.hora_inicial)}-${formatHora(v.ida.hora_final)}` : '',
      v.ida?.km_inicial?.toString() || '',
      v.volta?.origem || '',
      v.volta?.destino || '',
      v.volta ? `${formatHora(v.volta.hora_inicial)}-${formatHora(v.volta.hora_final)}` : '',
      v.volta?.km_inicial?.toString() || '',
    ];
  });

  autoTable(doc, {
    startY: 60,
    head,
    body,
    styles: { fontSize: 8 },
    headStyles: {
      fillColor: [100, 100, 100],
      textColor: 255,
    },
    columnStyles: {
      0: { cellWidth: 20 },
      1: { cellWidth: 25 },
      2: { cellWidth: 25 },
      3: { cellWidth: 20 },
      4: { cellWidth: 15 },
      5: { cellWidth: 25 },
      6: { cellWidth: 25 },
      7: { cellWidth: 20 },
      8: { cellWidth: 15 },
    },
  });

  const pdfBlob = doc.output('blob');
  const fileName = `diariodebordo-${semana}-${Date.now()}.pdf`;

  const { data, error } = await supabase.storage
    .from('pdfs')
    .upload(fileName, pdfBlob, {
      cacheControl: '3600',
      upsert: true,
      contentType: 'application/pdf',
    });

  if (error) {
    console.error('Erro ao fazer upload do PDF:', error);
    return null;
  }

  const { data: urlData } = supabase.storage.from('pdfs').getPublicUrl(fileName);
  return urlData?.publicUrl || null;
}
